(1926, 138, '_benefist_loop_5_benefist_loop_cnt', 'field_63a2c66e5236b'),
(1927, 138, 'benefist_cat_faq_0_benefist_cat_faq_que', 'Potrzebujesz prywatnej opieki lekarskiej lub konsultacji ze specjalistą'),
