(2310, 141, '_benefist_loop_5_benefist_loop_cnt', 'field_63a2c66e5236b'),
(2311, 141, 'benefist_cat_faq_0_benefist_cat_faq_que', 'Potrzebujesz prywatnej opieki lekarskiej lub konsultacji ze specjalistą'),
(2312, 141, '_benefist_cat_faq_0_benefist_cat_faq_que', 'field_63a2d4e300b79'),
(2313, 141, 'benefist_cat_faq_0_benefist_cat_faq_ans', '<div class=\"thesis-dropdownstyled__SubTitle-sc-zl4xx2-3 foZTfP\"><strong>Usługi medyczne</strong></div>\r\n<p class=\"thesis-dropdownstyled__Text-sc-zl4xx2-4 DJTQh\">Z Efectivo masz dostęp do profesjonalnego zespołu medycznego w placówkach w całej Polsce, konsultacji internistycznych, teleporad, podstawowych badań diagnostycznych, laboratoryjnych i profilaktycznych.</p>'),
(2314, 141, '_benefist_cat_faq_0_benefist_cat_faq_ans', 'field_63a2d4fa00b7a'),
(2315, 141, 'benefist_cat_faq_1_benefist_cat_faq_que', 'Chcesz otrzymywać dodatkowe benefity pieniężne?'),
(2316, 141, '_benefist_cat_faq_1_benefist_cat_faq_que', 'field_63a2d4e300b79'),
(2317, 141, 'benefist_cat_faq_1_benefist_cat_faq_ans', '<div class=\"thesis-dropdownstyled__SubTitle-sc-zl4xx2-3 foZTfP\"><strong>Benefity pieniężne</strong></div>\r\n<p class=\"thesis-dropdownstyled__Text-sc-zl4xx2-4 DJTQh\">Z nowoczesną wirtualną kartą przedpłaconą możesz płacić we wszystkich punktach w Polsce i na całym świecie. Pozwala ona także na wypłaty zbliżeniowe z bankomatów oraz płatności online.</p>'),
(2318, 141, '_benefist_cat_faq_1_benefist_cat_faq_ans', 'field_63a2d4fa00b7a'),
(2319, 141, 'benefist_cat_faq_2_benefist_cat_faq_que', 'Chciałbyś rozwijać się zawodowo, bo marzy Ci się awans?'),
(2320, 141, '_benefist_cat_faq_2_benefist_cat_faq_que', 'field_63a2d4e300b79'),
(2321, 141, 'benefist_cat_faq_2_benefist_cat_faq_ans', '<div class=\"thesis-dropdownstyled__SubTitle-sc-zl4xx2-3 foZTfP\"><strong>Szkolenia i kursy</strong></div>\r\n<p class=\"thesis-dropdownstyled__Text-sc-zl4xx2-4 DJTQh\">Efectivo daje Ci możliwość podniesienia kwalifikacji zawodowych i kompetencji. Wybieraj z oferty stacjonarnych profesjonalnych szkoleń UDT i online’owych kursów językowych.</p>'),
(2322, 141, '_benefist_cat_faq_2_benefist_cat_faq_ans', 'field_63a2d4fa00b7a'),
(2323, 141, 'benefist_cat_faq_3_benefist_cat_faq_que', 'Chcesz kupować taniej?'),
(2324, 141, '_benefist_cat_faq_3_benefist_cat_faq_que', 'field_63a2d4e300b79'),
(2325, 141, 'benefist_cat_faq_3_benefist_cat_faq_ans', '<strong>Zniżki i rabaty do popularnych sklepów</strong>\r\n\r\nzeroka oferta promocji, rabatów i kodów zniżkowych dostępna w Efectivo gwarantuje oszczędności na codziennych zakupach w najpopularniejszych sklepach, zarówno stacjonarnych, jak i online.'),
(2326, 141, '_benefist_cat_faq_3_benefist_cat_faq_ans', 'field_63a2d4fa00b7a'),
(2327, 141, 'benefist_cat_faq_4_benefist_cat_faq_que', 'Czujesz się zmęczony, wypalony i brakuje Ci motywacji do pracy?'),
(2328, 141, '_benefist_cat_faq_4_benefist_cat_faq_que', 'field_63a2d4e300b79'),
(2329, 141, 'benefist_cat_faq_4_benefist_cat_faq_ans', '<div class=\"thesis-dropdownstyled__SubTitle-sc-zl4xx2-3 foZTfP\"><strong>Usługi psychologiczne</strong></div>\r\n<p class=\"thesis-dropdownstyled__Text-sc-zl4xx2-4 DJTQh\">Z Efectivo oferuje wsparcie psychologiczne. Dzięki rozmowom ze specjalistami odzyskasz spokój, równowagę, poprawisz relacje w pracy i nauczysz się rozwiązywać problemy osobiste i zawodowe.</p>'),
(2330, 141, '_benefist_cat_faq_4_benefist_cat_faq_ans', 'field_63a2d4fa00b7a'),
(2331, 141, 'benefist_cat_faq_5_benefist_cat_faq_que', 'Chciałbyś zaoszczędzić na wycieczce lub wakacjach?'),
(2332, 141, '_benefist_cat_faq_5_benefist_cat_faq_que', 'field_63a2d4e300b79'),
(2333, 141, 'benefist_cat_faq_5_benefist_cat_faq_ans', '<div class=\"thesis-dropdownstyled__SubTitle-sc-zl4xx2-3 foZTfP\"><strong>Wakacje i wycieczki</strong></div>\r\n<p class=\"thesis-dropdownstyled__Text-sc-zl4xx2-4 DJTQh\">Uzbierane dodatkowo pieniądze w aplikacji Efectivo możesz przeznaczyć na bony na wakcje w górach, nad morzem, nad jeziorem.</p>'),
